mapper.select:
SELECT MEMBER_NAME, EMAIL
FROM MEMBER
WHERE EMAIL = #{email} AND PWD = #{pwd}

SELECT MEMBER_NO, MEMBER_NAME, EMAIL, CRE_DATE
FROM MEMBER
WHERE MEMBER_NO = #{no}

mapper.update:
UPDATE MEMBER
EMAIL = #{email}, MEMBER_NAME = #{memberName}, MOD_DATE = SYSDATE
WHERE MEMBER_NO = #{memberNo}
